var test         = require('tape');
var randomUser = require('./../index');

test('returns a user object', function(t) {
  t.plan(2);

  randomUser()
    .then(function(res) {
      var hasName = res.hasOwnProperty('name');
      var hasLocation = res.hasOwnProperty('location');

      t.ok(hasName, 'user has a name');
      t.ok(hasLocation, 'user has a location');
    });
});

test('returns a simple user object', function(t) {
  t.plan(5);
  
  randomUser('simple')
    .then(function(res) {
      console.log(res);
      
      var hasName = res.hasOwnProperty('name');
      var hasEmail = res.hasOwnProperty('email');
      var hasUsername = res.hasOwnProperty('username');
      var hasPassword = res.hasOwnProperty('password');
      var noLocation = !res.hasOwnProperty('location');
      
      t.ok(hasName, 'simple user has a name');
      t.ok(hasEmail, 'simple user has an email');
      t.ok(hasUsername, 'simple user has a username');
      t.ok(hasPassword, 'simple user has a password');
      
      t.ok(noLocation, 'simple user does not have a location');
    });
});
